import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [page, setPage] = useState(1);
  const [followedUsers, setFollowedUsers] = useState([]);

  useEffect(() => {
    fetchUsers();
  }, [page]);

  useEffect(() => {
    const followedUsersData = JSON.parse(localStorage.getItem("followedUsers"));
    if (followedUsersData) {
      setFollowedUsers(followedUsersData);
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("followedUsers", JSON.stringify(followedUsers));
  }, [followedUsers]);

  const fetchUsers = async () => {
    try {
      const response = await axios.get(
        `https://649d9b6c9bac4a8e669e0435.mockapi.io/users`
      );
      setUsers(response.data);
    } catch (error) {
      console.log(error);
    }
  };

  const handleLoadMore = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const handleFollowUser = async (userId) => {
    const followedUser = users.find((user) => user.id === userId);
    if (followedUser) {
      if (followedUsers.includes(userId)) {
        setFollowedUsers((prevFollowedUsers) =>
          prevFollowedUsers.filter((id) => id !== userId)
        );
        const response = await axios.put(
          `https://649d9b6c9bac4a8e669e0435.mockapi.io/users/${userId}`,
          {
            followers: followedUser.followers - 1,
          }
        );
        if (response.status === 200) {
          const updatedUsers = users.map((user) => {
            if (user.id === userId) {
              return {
                ...user,
                followers: followedUser.followers - 1,
              };
            }
            return user;
          });
          setUsers(updatedUsers);
        }
      } else {
        setFollowedUsers((prevFollowedUsers) => [...prevFollowedUsers, userId]);
        const response = await axios.put(
          `https://649d9b6c9bac4a8e669e0435.mockapi.io/users/${userId}`,
          {
            followers: followedUser.followers + 1,
          }
        );
        if (response.status === 200) {
          const updatedUsers = users.map((user) => {
            if (user.id === userId) {
              return {
                ...user,
                followers: followedUser.followers + 1,
              };
            }
            return user;
          });
          setUsers(updatedUsers);
        }
      }
    }
  };

  return (
    <div>
      <h1>User List</h1>
      {users.map((user) => (
        <div key={user.id}>
          <img src={user.avatar} alt="User Avatar" />
          <h2>
            {followedUsers.includes(user.id)
              ? localStorage.getItem(`followers-${user.id}`)
              : user.followers}{" "}
            followers
          </h2>
          <h2>{user.tweets} tweets</h2>
          <h3>{user.user}</h3>
          <button onClick={() => handleFollowUser(user.id)}>
            {followedUsers.includes(user.id) ? "Followed" : "Follow"}
          </button>
        </div>
      ))}
      <button onClick={handleLoadMore}>Load More</button>
    </div>
  );
};

export default UserList;
