import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import Card from "../components/Card";

const Home = () => {
  const [users, setUsers] = useState([]);

  useEffect(() => {
    fetchUsers();
  }, []);

  const fetchUsers = async () => {
    try {
      const response = await fetch("https://api.mockapi.io/users");
      const data = await response.json();
      setUsers(data);
    } catch (error) {
      console.log("Error fetching users:", error);
    }
  };

  const handleFollow = async (userId, isFollowing) => {
    try {
      const userToUpdate = users.find((user) => user.id === userId);
      const updatedUser = { ...userToUpdate, isFollowing: !isFollowing };

      const response = await fetch(`https://api.mockapi.io/users/${userId}`, {
        method: "PUT",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify(updatedUser),
      });

      if (response.ok) {
        const updatedUsers = users.map((user) => {
          if (user.id === userId) {
            return updatedUser;
          }
          return user;
        });
        setUsers(updatedUsers);
      } else {
        console.log("Error updating user:", response.statusText);
      }
    } catch (error) {
      console.log("Error updating user:", error);
    }
  };

  return (
    <div>
      <h1>Home Page</h1>
      <Link to="/tweets">Go to Tweets</Link>
      {users.map((user) => (
        <Card key={user.id} user={user} handleFollow={handleFollow} />
      ))}
    </div>
  );
};

export default Home;
