import React from "react";

const UserCard = ({ user, isFollowed, onFollowUser }) => {
  const cardStyle = {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "4px",
    marginBottom: "20px",
  };

  const avatarStyle = {
    width: "100px",
    height: "100px",
    borderRadius: "50%",
    marginBottom: "10px",
  };

  const buttonStyle = {
    padding: "10px 20px",
    backgroundColor: isFollowed ? "green" : "blue",
    color: "#fff",
    border: "none",
    borderRadius: "4px",
    cursor: "pointer",
  };

  return (
    <div style={cardStyle}>
      <img src={user.avatar} alt="User Avatar" style={avatarStyle} />
      <h2>{isFollowed ? user.followers + 1 : user.followers} followers</h2>
      <h2>{user.tweets} tweets</h2>
      <h3>{user.user}</h3>
      <button onClick={() => onFollowUser(user.id)} style={buttonStyle}>
        {isFollowed ? "Followed" : "Follow"}
      </button>
    </div>
  );
};

export default UserCard;
