import React, { useState, useEffect } from "react";
import { Link } from "react-router-dom";
import axios from "axios";

const UserList = () => {
  const [users, setUsers] = useState([]);
  const [page, setPage] = useState(1);
  const [followedUsers, setFollowedUsers] = useState([]);

  useEffect(() => {
    fetchUsers();
    const storedFollowedUsers = localStorage.getItem("followedUsers");
    if (storedFollowedUsers) {
      setFollowedUsers(JSON.parse(storedFollowedUsers));
    }
  }, [page]);

  useEffect(() => {
    localStorage.setItem("followedUsers", JSON.stringify(followedUsers));
  }, [followedUsers]);

  const fetchUsers = async () => {
    try {
      const response = await axios.get(
        `https://649d9b6c9bac4a8e669e0435.mockapi.io/users`
      );
      setUsers(response.data);
      console.log(response);
    } catch (error) {
      console.log(error);
    }
  };

  const handleLoadMore = () => {
    setPage((prevPage) => prevPage + 1);
  };

  const handleFollowUser = (userId) => {
    const followedUser = users.find((user) => user.id === userId);
    if (followedUser) {
      const isFollowed = followedUsers.some(
        (followedUser) => followedUser.id === userId
      );
      if (isFollowed) {
        unfollowUser(userId);
      } else {
        followUser(followedUser);
      }
    }
  };

  const followUser = (user) => {
    const updatedFollowedUsers = [...followedUsers, user];
    setFollowedUsers(updatedFollowedUsers);
    updateFollowersCount(user.id, 1);
  };

  const unfollowUser = (userId) => {
    const updatedFollowedUsers = followedUsers.filter(
      (followedUser) => followedUser.id !== userId
    );
    setFollowedUsers(updatedFollowedUsers);
    updateFollowersCount(userId, -1);
  };

  const updateFollowersCount = (userId, count) => {
    setUsers((prevUsers) =>
      prevUsers.map((user) => {
        if (user.id === userId) {
          return {
            ...user,
            followers: user.followers + count,
          };
        }
        return user;
      })
    );
  };

  return (
    <div>
      <h1>User List</h1>
      {users.map((user) => (
        <div key={user.id}>
          <img src={user.avatar} alt="User Avatar" />
          <h2>{user.followers} followers</h2>
          <h2>{user.tweets} tweets</h2>
          <h3>{user.user}</h3>
          <button onClick={() => handleFollowUser(user.id)}>
            {followedUsers.some((followedUser) => followedUser.id === user.id)
              ? "Followed"
              : "Follow"}
          </button>
        </div>
      ))}
      <button onClick={handleLoadMore}>Load More</button>
    </div>
  );
};

export default UserList;
