import React, { useState } from "react";
import { BrowserRouter as Router, Route, Link } from "react-router-dom";
import HomePage from "./pages/Home/Home";
import TweetsPage from "./pages/Tweets/Tweets";

const App = () => {
  const [following, setFollowing] = useState(false);

  const handleToggleFollowing = () => {
    setFollowing((prevFollowing) => !prevFollowing);
  };

  return (
    <Router>
      <div>
        <nav>
          <ul>
            <li>
              <Link to="/">Home</Link>
            </li>
            <li>
              <Link to="/tweets">Tweets</Link>
            </li>
          </ul>
        </nav>

        {/* Render routes conditionally based on the current path */}
        {window.location.pathname === "/" && (
          <Route exact path="/" component={HomePage} />
        )}

        {window.location.pathname === "/tweets" && (
          <Route
            path="/tweets"
            render={() => (
              <TweetsPage
                following={following}
                onToggleFollowing={handleToggleFollowing}
              />
            )}
          />
        )}
      </div>
    </Router>
  );
};

export default App;
